import capitalize from "lodash/capitalize";
import { imageFromJson, imageQueryFields } from "../../core/models";
export function homeBlogItemFromJson(json, lang) {
  const lng = capitalize(lang);
  return {
    id: json.id,
    title: json[`title${lng}`],
    description: json[`description${lng}`],
    image: imageFromJson(json.image)
  };
}
export const HomeBlogService = {
  getQuery: lang => {
    const lng = capitalize(lang);
    return `
        posts (skip: 0, take: 3) {
            data {
                id
                title${lng}
                description${lng}
                image {
                    ${imageQueryFields}
                }
            }
        }
    `;
  }
};
