import capitalize from "lodash/capitalize";
import { imageFromJson, imageQueryFields } from "../../core/models";
export function homeBlogItemFromJson(json, lang) {
  var lng = capitalize(lang);
  return {
    id: json.id,
    title: json["title".concat(lng)],
    description: json["description".concat(lng)],
    image: imageFromJson(json.image)
  };
}
export var HomeBlogService = {
  getQuery: function getQuery(lang) {
    var lng = capitalize(lang);
    return "\n        posts (skip: 0, take: 3) {\n            data {\n                id\n                title".concat(lng, "\n                description").concat(lng, "\n                image {\n                    ").concat(imageQueryFields, "\n                }\n            }\n        }\n    ");
  }
};
